mapper.select:
select d.id "id",
d.disease_group_id "diseaseGroupId",
d.group_one "groupOne",
d.group_two "groupTwo",
d.item_name "itemName",
d.item_value "itemValue",
d.data_type "dataType"
from tb_pati_disease_detail d
where 1=1

	and d.pati_serial =#{patiSerial}


	and d.inpati_number =#{inpatiNumber}


	and d.detail_id =#{detailId}


	and to_char(d.operate_date, 'yyyy-mm-dd') = #{date}

select A.Detail_Id "detailId", A.Pati_Serial "patiSerial", A.Inpati_Number "inpatiNumber", A.medical_insurance_code "medicalInsuranceCode", to_char(A.operate_date, 'yyyy-mm-dd') "operateDate", A.Disease_Name_Id "diseaseNameId", A.name "name", A.disease_name "diseaseName" from (select d.detail_id, d.pati_serial, d.inpati_number, d.medical_insurance_code, d.operate_date, d.disease_name_id, d.name, dict.disease_name from tb_pati_disease_detail d,tb_disease_name_dict dict where 1 = 1 and dict.disease_name_id=d.disease_name_id
and d.medical_insurance_code =#{params.medicalInsuranceCode}
and d.inpati_number =#{params.inpatiNumber}
and d.name like '%'||#{params.name}||'%'
group by d.detail_id, d.pati_serial, d.inpati_number, d.medical_insurance_code, d.operate_date, d.disease_name_id, d.name, dict.disease_name order by d.operate_date desc ) A
