mapper.select:
select t.flup_name_id flupNameId, t.flup_name flupName, t.flup_type flupType,t.dept_code deptCode,t.diag_code diagCode, t.diag_name diagName,t.operate_date operateDate, t.employee_code employeeCode from tb_flup_name_dict t where t.use_state = 1
and t.dept_code in
#{item}

select a.flup_name_id "flupNameId", a.flup_name "flupName", a.flup_type "flupType", a.dept_code "deptCode", f_get_dept_name(a.dept_code) "deptName", a.diag_name "diagName", a.diag_code "diagCode", to_char(a.operate_date,'yyyy-mm-dd') "operateDate", b.name "employeeCode", a.use_state "useState", c.unit_code "unitCode", a.use_state "buttonUseState" from tb_flup_name_dict a left join tb_employee b on a.employee_code=b.jobnum left join tb_department c on a.dept_code= c.dept_code where 1=1
and a.flup_name like '%${params.flupName}%'
and a.diag_name=#{params.diagName}
and a.flup_type=#{params.flupType}
and a.dept_code in
#{item.deptCode}
order by a.operate_date desc
